			JobDict.Add(11000001, new JobConfig(11000001,"战士","swordhit","战士是游戏中必不可少的角色之一，或者说，在许多奇幻游戏中都必然有战士的身影，他是团队的主力，依靠丰富的战斗经验帮助队伍战胜敌人。战士很可能身经百战，并且有着严格的纪律约束，通常战士是最能稳定团队情绪的人。",new int[]{45,25,30},new int[]{53000001},new int[]{22032007},new int[]{21400101},"1"));
			JobDict.Add(11000002, new JobConfig(11000002,"护卫","swordhit","护卫是以近身格斗为主的职业。这些角色象钉子一样坚韧，精通各种武器和战术技巧。他们拥有和战斗相关的各种特殊能力。战士是为战斗而生的终极格斗机器。",new int[]{45,25,30},new int[]{53000002},new int[]{22032007},new int[]{},"2"));
			JobDict.Add(11000003, new JobConfig(11000003,"盗贼","swordhit","他们可以按照自己的计划改变事件的走向，只有在拥有极大优势的时候才发起攻击，潜行者们老到的计谋、过人的本领、优秀的欺骗和隐藏能力使他们可以在担任窃贼、谋杀者、间谍甚至刺客时无往而不利。",new int[]{45,25,30},new int[]{53000003,53000004},new int[]{},new int[]{},"3"));
			JobDict.Add(11000004, new JobConfig(11000004,"射手","arrow","每种生物都和猎人有着特殊的联系。猎人可以跟踪, 驯服和杀死野外发现的各种动物和野兽。不管猎人用弓箭还是用火枪, 他们都只把他们的武器和宠物看作真正的朋友。 ",new int[]{45,25,30},new int[]{},new int[]{},new int[]{21400101},"4"));
			JobDict.Add(11000005, new JobConfig(11000005,"游侠","swordhit","游侠擅长各种武器，精于战斗。远程攻击是他的拿手好戏，能在瞬间精准射击。他具备寻找猎物，躲开侦察的技能。而且经验老道的游侠与大自然十分亲近，可利用大自然的力量施展神术。",new int[]{50,20,30},new int[]{},new int[]{},new int[]{},"5"));
			JobDict.Add(11000006, new JobConfig(11000006,"法师","fireball","在魔法占据主导地位的艾伯伦世界，法师与其他魔法爱好者完全不同，他们可以为枯燥的学习过程奉献终生。法师更具灵活性――经验丰富的法师甚至能依据不同情况准备百余种法术。",new int[]{35,15,50},new int[]{},new int[]{},new int[]{},"6"));
			JobDict.Add(11000007, new JobConfig(11000007,"贤者","leafarrow","幻兽传说中的牧师，可绝非是脆弱的布衣族，他们身穿重甲，手握神圣武器，往往是居于战场的前列，特别是面对不死生物时，更是挺身而出掩护其他队友。无怪乎牧师经常成为最受欢迎的职业了。",new int[]{40,15,45},new int[]{},new int[]{},new int[]{},"7"));
			JobDict.Add(11000008, new JobConfig(11000008,"诗人","moon","吟游诗人通常并不甘于街头卖艺这种简单工作。许多吟游诗人依靠魔法技巧发掘古老的传说，还能运用人际关系及音乐能力充任间谍，大使或是历史学者。同时，吟游诗人也常常能成为冒险者，甚至在政府或是家族商会中找到工作。",new int[]{45,15,40},new int[]{},new int[]{},new int[]{},"8"));
		}
