		private static void LoadJob()
		{
			JobDict.Add(1, new JobConfig(1,"战士",20,12,0,10,5,3,12,8,20,10,5,10,10,10,15,20,"swordhit",20,10,5,10,10,0,0,10,10,0,10,0,15,"战士是游戏中必不可少的角色之一，或者说，在许多奇幻游戏中都必然有战士的身影，他是团队的主力，依靠丰富的战斗经验帮助队伍战胜敌人。战士很可能身经百战，并且有着严格的纪律约束，但并不像圣骑士那样死板，像法师那样高傲，通常战士是最能稳定团队情绪的人。",new int[]{1000}));
			JobDict.Add(2, new JobConfig(2,"护卫",5,18,0,15,0,0,17,15,10,20,5,10,5,10,20,20,"swordhit",10,20,5,5,10,0,15,0,15,0,10,10,0,"护卫是以近身格斗为主的职业。这些角色象钉子一样坚韧，精通各种武器和战术技巧。他们拥有和战斗相关的各种特殊能力。战士是为战斗而生的终极格斗机器。",new int[]{1010}));
			JobDict.Add(3, new JobConfig(3,"盗贼",10,3,2,15,25,10,0,5,10,5,10,15,25,15,10,10,"swordhit",10,0,10,5,20,10,0,15,0,0,10,10,10,"他们可以按照自己的计划改变事件的走向，只有在拥有极大优势的时候才发起攻击，潜行者们老到的计谋、过人的本领、优秀的欺骗和隐藏能力使他们可以在担任窃贼、谋杀者、间谍甚至刺客时无往而不利。",new int[]{1020}));
			JobDict.Add(4, new JobConfig(4,"射手",10,5,5,10,12,13,0,15,20,10,10,15,10,10,15,10,"arrow",10,0,20,10,5,10,10,0,0,10,0,15,10,"每种生物都和猎人有着特殊的联系。猎人可以跟踪, 驯服和杀死野外发现的各种动物和野兽。不管猎人用弓箭还是用火枪, 他们都只把他们的武器和宠物看作真正的朋友。 ",new int[]{1030}));
			JobDict.Add(5, new JobConfig(5,"游侠",15,10,0,22,18,0,5,0,15,10,5,25,15,10,10,10,"swordhit",15,10,0,20,10,5,0,10,10,0,0,10,10,"游侠擅长各种武器，精于战斗。远程攻击是他的拿手好戏，能在瞬间精准射击。他具备寻找猎物，躲开侦察的技能。而且经验老道的游侠与大自然十分亲近，可利用大自然的力量施展神术。",new int[]{1040}));
			JobDict.Add(6, new JobConfig(6,"法师",12,3,27,8,5,10,0,5,15,5,25,15,5,15,10,10,"fireball",5,0,15,0,5,20,10,10,0,15,10,10,0,"在魔法占据主导地位的艾伯伦世界，法师与其他魔法爱好者完全不同，他们可以为枯燥的学习过程奉献终生。相对术士和龙纹继承者，法师更具灵活性――经验丰富的法师甚至能依据不同情况准备百余种法术。相对其他职业，法师特别热衷探索泽恩得瑞克大陆失落的秘密。古老的遗迹中也许埋藏着失传的魔法，等待着法师的光顾。",new int[]{1050}));
			JobDict.Add(7, new JobConfig(7,"贤者",3,8,22,10,0,0,12,15,10,15,20,5,10,15,15,10,"leafarrow",0,10,10,5,0,15,20,5,10,15,0,0,10,"幻兽传说中的牧师，可绝非是脆弱的布衣族，他们身穿重甲，手握神圣武器，往往是居于战场的前列，特别是面对不死生物时，更是挺身而出掩护其他队友。无怪乎牧师经常成为最受欢迎的职业了。",new int[]{1060}));
			JobDict.Add(8, new JobConfig(8,"诗人",5,3,15,10,10,20,5,2,5,10,20,10,15,25,10,5,"moon",0,5,0,10,10,10,10,20,0,10,10,15,0,"吟游诗人通常并不甘于街头卖艺这种简单工作。许多吟游诗人依靠魔法技巧发掘古老的传说，还有些运用人际关系及音乐能力充任间谍，大使，外交官或是历史学者。同理，吟游诗人也常常能成为冒险者，甚至在政府或是家族商会中找到工作。",new int[]{1070}));
		}
